'use strict';

document.addEventListener("DOMContentLoaded", function () {

    // define constants
    const body = document.body;

    let addBodyClass = function () {

        if (body !== null) {

            body.classList.add('tuxendo-active');
        }
    }

    let __initAllTuxendo = function () {

        addBodyClass();
    }

    __initAllTuxendo();
});